-- 2. VISTAS OPTIMIZADAS
-- =====================================================

-- TÍTULO: Vista para Predicción de Diabetes
-- DESCRIPCIÓN: Vista que obtiene los datos más recientes para predicción de diabetes usando colesterol como indicador

CREATE OR REPLACE VIEW v_datos_prediccion_diabetes AS
WITH datos_recientes AS (
    SELECT DISTINCT ON (id_usuario) 
        id_usuario, fecha_nacimiento, sexo, fecha
    FROM Datos_Personales 
    ORDER BY id_usuario, fecha DESC
),
historial_reciente AS (
    SELECT DISTINCT ON (id_usuario) 
        id_usuario, bmi, colesterol, fecha
    FROM Historial_Medico 
    ORDER BY id_usuario, fecha DESC
),
estilo_reciente AS (
    SELECT DISTINCT ON (id_usuario) 
        id_usuario, nivel_actividad_fisica, alcohol, fecha
    FROM Estilo_Vida 
    ORDER BY id_usuario, fecha DESC
)
SELECT 
    u.id_usuario,
    u.email,
    dr.fecha_nacimiento,
    dr.sexo,
    hr.bmi,
    hr.colesterol,
    er.nivel_actividad_fisica,
    er.alcohol,
    GREATEST(
        COALESCE(dr.fecha, '1900-01-01'::timestamp),
        COALESCE(hr.fecha, '1900-01-01'::timestamp),
        COALESCE(er.fecha, '1900-01-01'::timestamp)
    ) as fecha_actualizacion_mas_reciente
FROM Usuario u
LEFT JOIN datos_recientes dr ON u.id_usuario = dr.id_usuario
LEFT JOIN historial_reciente hr ON u.id_usuario = hr.id_usuario
LEFT JOIN estilo_reciente er ON u.id_usuario = er.id_usuario;

-- TÍTULO: Vista para Predicción de Hipertensión
-- DESCRIPCIÓN: Vista que obtiene los datos más recientes para predicción de hipertensión usando colesterol_alto como indicador

CREATE OR REPLACE VIEW v_datos_prediccion_hipertension AS
WITH datos_recientes AS (
    SELECT DISTINCT ON (id_usuario) 
        id_usuario, fecha_nacimiento, fecha
    FROM Datos_Personales 
    ORDER BY id_usuario, fecha DESC
),
historial_reciente AS (
    SELECT DISTINCT ON (id_usuario) 
        id_usuario, bmi, presion_arterial, colesterol_alto, fecha
    FROM Historial_Medico 
    ORDER BY id_usuario, fecha DESC
),
estilo_reciente AS (
    SELECT DISTINCT ON (id_usuario) 
        id_usuario, consumo_sal, tabaco, horas_dormir, nivel_estres, nivel_actividad_fisica, fecha
    FROM Estilo_Vida 
    ORDER BY id_usuario, fecha DESC
),
medicamentos_recientes AS (
    SELECT 
        h.id_usuario,
        STRING_AGG(m.nombre, ', ') as nombres_medicamentos
    FROM Historial_Medicamento hm
    JOIN Medicamento m ON hm.id_medicamento = m.id_medicamento
    JOIN Historial_Medico h ON hm.id_historial = h.id_historial
    WHERE h.fecha = (
        SELECT MAX(fecha) 
        FROM Historial_Medico h2 
        WHERE h2.id_usuario = h.id_usuario
    )
    GROUP BY h.id_usuario
)
SELECT 
    u.id_usuario,
    u.email,
    dr.fecha_nacimiento,
    hr.bmi,
    hr.presion_arterial,
    hr.colesterol_alto,
    COALESCE(mr.nombres_medicamentos, 'Ninguna') as nombres_medicamentos,
    er.consumo_sal,
    er.tabaco,
    er.horas_dormir,
    er.nivel_estres,
    er.nivel_actividad_fisica,
    GREATEST(
        COALESCE(dr.fecha, '1900-01-01'::timestamp),
        COALESCE(hr.fecha, '1900-01-01'::timestamp),
        COALESCE(er.fecha, '1900-01-01'::timestamp)
    ) as fecha_actualizacion_mas_reciente
FROM Usuario u
LEFT JOIN datos_recientes dr ON u.id_usuario = dr.id_usuario
LEFT JOIN historial_reciente hr ON u.id_usuario = hr.id_usuario
LEFT JOIN estilo_reciente er ON u.id_usuario = er.id_usuario
LEFT JOIN medicamentos_recientes mr ON u.id_usuario = mr.id_usuario;

-- TÍTULO: Vista de Enfermedades del Usuario
-- DESCRIPCIÓN: Vista que muestra todas las enfermedades de cada usuario basada en colesterol y colesterol_alto

CREATE OR REPLACE VIEW v_enfermedades_usuario AS
SELECT 
    u.id_usuario,
    u.email,
    e.nombre as nombre_enfermedad,
    h.fecha as fecha_diagnostico,
    CASE 
        WHEN h.colesterol = true THEN 'Activa'
        WHEN h.colesterol_alto = true THEN 'Activa'
        ELSE 'Inactiva'
    END as estado
FROM Usuario u
JOIN Historial_Medico h ON u.id_usuario = h.id_usuario
JOIN Historial_Enfermedad he ON h.id_historial = he.id_historial
JOIN Enfermedad e ON he.id_enfermedad = e.id_enfermedad;

-- TÍTULO: Vista de Recomendaciones para Diabetes
-- DESCRIPCIÓN: Vista que obtiene solo las recomendaciones para diabetes

CREATE OR REPLACE VIEW v_recomendaciones_diabetes AS
SELECT 
    r.titulo as titulo_recomendacion,
    r.descripcion as descripcion_recomendacion,
    CURRENT_TIMESTAMP as fecha_recomendaciones
FROM Recomendacion r
WHERE r.id_recomendacion IN (
    SELECT er.id_recomendacion 
    FROM Enfermedad_Recomendacion er
    JOIN Enfermedad e ON er.id_enfermedad = e.id_enfermedad
    WHERE e.nombre = 'Diabetes'
);

-- TÍTULO: Vista de Recomendaciones para Hipertensión
-- DESCRIPCIÓN: Vista que obtiene solo las recomendaciones para hipertensión

CREATE OR REPLACE VIEW v_recomendaciones_hipertension AS
SELECT 
    r.titulo as titulo_recomendacion,
    r.descripcion as descripcion_recomendacion,
    CURRENT_TIMESTAMP as fecha_recomendaciones
FROM Recomendacion r
WHERE r.id_recomendacion IN (
    SELECT er.id_recomendacion 
    FROM Enfermedad_Recomendacion er
    JOIN Enfermedad e ON er.id_enfermedad = e.id_enfermedad
    WHERE e.nombre = 'Hipertensión'
);

