-- =====================================================
-- 1. STORED PROCEDURE: ANÁLISIS DE DOCUMENTOS MÉDICOS SUBIDOS
-- =====================================================
-- TÍTULO: Stored Procedure para Análisis Completo de Documentos Médicos
-- DESCRIPCIÓN: Analiza todos los documentos médicos subidos por el usuario
-- con JOINs múltiples y agregaciones de datos reales

CREATE OR REPLACE PROCEDURE analizar_documentos_medicos(
    p_usuario_id UUID,
    INOUT resultado REFCURSOR
)
LANGUAGE plpgsql AS $$
BEGIN
    OPEN resultado FOR
    SELECT 
    u.id_usuario,
    u.email,
    
    -- Conteo de documentos por tipo
    COUNT(DISTINCT ds.id_subido) as total_documentos_subidos,
    COUNT(DISTINCT CASE WHEN d.nombre = 'Hemoglobina Glicada (HbA1c)' THEN ds.id_subido END) as documentos_hba1c,
    COUNT(DISTINCT CASE WHEN d.nombre = 'Curva de Tolerancia a la Glucosa' THEN ds.id_subido END) as documentos_curva_tolerancia,
    COUNT(DISTINCT CASE WHEN d.nombre = 'Perfil Lipídico' THEN ds.id_subido END) as documentos_perfil_lipidico,
    COUNT(DISTINCT CASE WHEN d.nombre = 'Panel Metabólico' THEN ds.id_subido END) as documentos_panel_metabolico,
    COUNT(DISTINCT CASE WHEN d.nombre = 'Registros de Monitoreo de Presión Arterial (PA)' THEN ds.id_subido END) as documentos_monitoreo_pa,
    COUNT(DISTINCT CASE WHEN d.nombre = 'Consulta' THEN ds.id_subido END) as documentos_consulta,
    
    -- Valores más recientes de Hemoglobina Glicada
    (
        SELECT hg.hba1c 
        FROM Hemoglobina_Glicada hg
        JOIN Documento_Subido ds_hg ON hg.id_subido = ds_hg.id_subido
        WHERE ds_hg.id_usuario = u.id_usuario
        ORDER BY ds_hg.fecha_subido DESC
        LIMIT 1
    ) as ultima_hba1c,
    
    -- Valores más recientes de Curva de Tolerancia
    (
        SELECT ct.glucosa_ayunas 
        FROM Curva_Tolerancia ct
        JOIN Documento_Subido ds_ct ON ct.id_subido = ds_ct.id_subido
        WHERE ds_ct.id_usuario = u.id_usuario
        ORDER BY ds_ct.fecha_subido DESC
        LIMIT 1
    ) as ultima_glucosa_ayunas,
    
    -- Valores más recientes de Perfil Lipídico
    (
        SELECT pl.colesterol_LDL 
        FROM Perfil_Lipidico pl
        JOIN Documento_Subido ds_pl ON pl.id_subido = ds_pl.id_subido
        WHERE ds_pl.id_usuario = u.id_usuario
        ORDER BY ds_pl.fecha_subido DESC
        LIMIT 1
    ) as ultimo_colesterol_ldl,
    
    (
        SELECT pl.colesterol_HDL 
        FROM Perfil_Lipidico pl
        JOIN Documento_Subido ds_pl ON pl.id_subido = ds_pl.id_subido
        WHERE ds_pl.id_usuario = u.id_usuario
        ORDER BY ds_pl.fecha_subido DESC
        LIMIT 1
    ) as ultimo_colesterol_hdl,
    
    -- Valores más recientes de Panel Metabólico
    (
        SELECT pm.glucosa_ayunas 
        FROM Panel_Metabolico pm
        JOIN Documento_Subido ds_pm ON pm.id_subido = ds_pm.id_subido
        WHERE ds_pm.id_usuario = u.id_usuario
        ORDER BY ds_pm.fecha_subido DESC
        LIMIT 1
    ) as ultima_glucosa_panel,
    
    (
        SELECT pm.creatinina 
        FROM Panel_Metabolico pm
        JOIN Documento_Subido ds_pm ON pm.id_subido = ds_pm.id_subido
        WHERE ds_pm.id_usuario = u.id_usuario
        ORDER BY ds_pm.fecha_subido DESC
        LIMIT 1
    ) as ultima_creatinina,
    
    -- Valores más recientes de Monitoreo PA
    (
        SELECT mpa.PA_sistólica_promedio 
        FROM Monitoreo_PA mpa
        JOIN Documento_Subido ds_mpa ON mpa.id_subido = ds_mpa.id_subido
        WHERE ds_mpa.id_usuario = u.id_usuario
        ORDER BY ds_mpa.fecha_subido DESC
        LIMIT 1
    ) as ultima_pa_sistolica,
    
    -- Diagnósticos confirmados en consultas
    (
        SELECT c.diabtes_confirmada 
        FROM Consulta c
        JOIN Documento_Subido ds_c ON c.id_subido = ds_c.id_subido
        WHERE ds_c.id_usuario = u.id_usuario
        ORDER BY ds_c.fecha_subido DESC
        LIMIT 1
    ) as diabetes_confirmada,
    
    (
        SELECT c.hipertencion_confirmada 
        FROM Consulta c
        JOIN Documento_Subido ds_c ON c.id_subido = ds_c.id_subido
        WHERE ds_c.id_usuario = u.id_usuario
        ORDER BY ds_c.fecha_subido DESC
        LIMIT 1
    ) as hipertension_confirmada,
    
    -- Fecha del documento más reciente
    MAX(ds.fecha_subido) as fecha_documento_mas_reciente

FROM Usuario u
LEFT JOIN Documento_Subido ds ON u.id_usuario = ds.id_usuario
LEFT JOIN Documento d ON ds.id_documento = d.id_documento
WHERE u.id_usuario = p_usuario_id
GROUP BY u.id_usuario, u.email;
END;
$$;

-- =====================================================
-- 2. STORED PROCEDURE: ANÁLISIS DE TRATAMIENTOS Y MEDICAMENTOS
-- =====================================================
-- TÍTULO: Stored Procedure para Análisis de Tratamientos Activos y Medicamentos
-- DESCRIPCIÓN: Analiza los tratamientos confirmados y medicamentos del usuario

CREATE OR REPLACE PROCEDURE analizar_tratamientos_medicamentos(
    p_usuario_id UUID,
    INOUT resultado REFCURSOR
)
LANGUAGE plpgsql AS $$
BEGIN
    OPEN resultado FOR
    SELECT 
    u.id_usuario,
    u.email,
    
    -- Tratamientos confirmados en consultas
    (
        SELECT c.tratamiento_metformina 
        FROM Consulta c
        JOIN Documento_Subido ds ON c.id_subido = ds.id_subido
        WHERE ds.id_usuario = u.id_usuario
        ORDER BY ds.fecha_subido DESC
        LIMIT 1
    ) as tratamiento_metformina,
    
    (
        SELECT c.tratamiento_insulina 
        FROM Consulta c
        JOIN Documento_Subido ds ON c.id_subido = ds.id_subido
        WHERE ds.id_usuario = u.id_usuario
        ORDER BY ds.fecha_subido DESC
        LIMIT 1
    ) as tratamiento_insulina,
    
    (
        SELECT c.tratamiento_losartan 
        FROM Consulta c
        JOIN Documento_Subido ds ON c.id_subido = ds.id_subido
        WHERE ds.id_usuario = u.id_usuario
        ORDER BY ds.fecha_subido DESC
        LIMIT 1
    ) as tratamiento_losartan,
    
    (
        SELECT c.tratamiento_amlodipino 
        FROM Consulta c
        JOIN Documento_Subido ds ON c.id_subido = ds.id_subido
        WHERE ds.id_usuario = u.id_usuario
        ORDER BY ds.fecha_subido DESC
        LIMIT 1
    ) as tratamiento_amlodipino,
    
    -- Complicaciones detectadas
    (
        SELECT c.complicacion_retinopatia 
        FROM Consulta c
        JOIN Documento_Subido ds ON c.id_subido = ds.id_subido
        WHERE ds.id_usuario = u.id_usuario
        ORDER BY ds.fecha_subido DESC
        LIMIT 1
    ) as complicacion_retinopatia,
    
    (
        SELECT c.complicacion_neuropatia 
        FROM Consulta c
        JOIN Documento_Subido ds ON c.id_subido = ds.id_subido
        WHERE ds.id_usuario = u.id_usuario
        ORDER BY ds.fecha_subido DESC
        LIMIT 1
    ) as complicacion_neuropatia,
    
    (
        SELECT c.complicion_nefropatia 
        FROM Consulta c
        JOIN Documento_Subido ds ON c.id_subido = ds.id_subido
        WHERE ds.id_usuario = u.id_usuario
        ORDER BY ds.fecha_subido DESC
        LIMIT 1
    ) as complicacion_nefropatia,
    
    -- Medicamentos en historial médico
    COALESCE(
        STRING_AGG(DISTINCT m.nombre, ', '), 
        'Ninguno'
    ) as medicamentos_historial,
    
    -- Conteo de medicamentos
    COUNT(DISTINCT hm.id_medicamento) as total_medicamentos,
    
    -- Fecha de última consulta
    (
        SELECT MAX(ds.fecha_subido)
        FROM Consulta c
        JOIN Documento_Subido ds ON c.id_subido = ds.id_subido
        WHERE ds.id_usuario = u.id_usuario
    ) as fecha_ultima_consulta

FROM Usuario u
LEFT JOIN Historial_Medico hm ON u.id_usuario = hm.id_usuario
LEFT JOIN Historial_Medicamento hm_med ON hm.id_historial = hm_med.id_historial
LEFT JOIN Medicamento m ON hm_med.id_medicamento = m.id_medicamento
WHERE u.id_usuario = p_usuario_id
GROUP BY u.id_usuario, u.email;
END;
$$;

-- =====================================================
-- 3. STORED PROCEDURE: EVOLUCIÓN TEMPORAL DE PARÁMETROS MÉDICOS
-- =====================================================
-- TÍTULO: Stored Procedure para Evolución Temporal de Parámetros Médicos
-- DESCRIPCIÓN: Analiza la evolución de parámetros médicos a lo largo del tiempo

CREATE OR REPLACE PROCEDURE analizar_evolucion_parametros(
    p_usuario_id UUID,
    INOUT resultado REFCURSOR
)
LANGUAGE plpgsql AS $$
BEGIN
    OPEN resultado FOR
    SELECT 
    u.id_usuario,
    u.email,
    
    -- Evolución de Hemoglobina Glicada
    (
        SELECT AVG(hg.hba1c)
        FROM Hemoglobina_Glicada hg
        JOIN Documento_Subido ds ON hg.id_subido = ds.id_subido
        WHERE ds.id_usuario = u.id_usuario
        AND ds.fecha_subido >= CURRENT_DATE - INTERVAL '6 months'
    ) as hba1c_promedio_6_meses,
    
    (
        SELECT MIN(hg.hba1c)
        FROM Hemoglobina_Glicada hg
        JOIN Documento_Subido ds ON hg.id_subido = ds.id_subido
        WHERE ds.id_usuario = u.id_usuario
        AND ds.fecha_subido >= CURRENT_DATE - INTERVAL '6 months'
    ) as hba1c_minimo_6_meses,
    
    (
        SELECT MAX(hg.hba1c)
        FROM Hemoglobina_Glicada hg
        JOIN Documento_Subido ds ON hg.id_subido = ds.id_subido
        WHERE ds.id_usuario = u.id_usuario
        AND ds.fecha_subido >= CURRENT_DATE - INTERVAL '6 months'
    ) as hba1c_maximo_6_meses,
    
    -- Evolución de Glucosa en Ayunas
    (
        SELECT AVG(ct.glucosa_ayunas)
        FROM Curva_Tolerancia ct
        JOIN Documento_Subido ds ON ct.id_subido = ds.id_subido
        WHERE ds.id_usuario = u.id_usuario
        AND ds.fecha_subido >= CURRENT_DATE - INTERVAL '6 months'
    ) as glucosa_promedio_6_meses,
    
    -- Evolución de Colesterol
    (
        SELECT AVG(pl.colesterol_LDL)
        FROM Perfil_Lipidico pl
        JOIN Documento_Subido ds ON pl.id_subido = ds.id_subido
        WHERE ds.id_usuario = u.id_usuario
        AND ds.fecha_subido >= CURRENT_DATE - INTERVAL '6 months'
    ) as ldl_promedio_6_meses,
    
    (
        SELECT AVG(pl.colesterol_HDL)
        FROM Perfil_Lipidico pl
        JOIN Documento_Subido ds ON pl.id_subido = ds.id_subido
        WHERE ds.id_usuario = u.id_usuario
        AND ds.fecha_subido >= CURRENT_DATE - INTERVAL '6 months'
    ) as hdl_promedio_6_meses,
    
    -- Evolución de Presión Arterial
    (
        SELECT AVG(mpa.PA_sistólica_promedio)
        FROM Monitoreo_PA mpa
        JOIN Documento_Subido ds ON mpa.id_subido = ds.id_subido
        WHERE ds.id_usuario = u.id_usuario
        AND ds.fecha_subido >= CURRENT_DATE - INTERVAL '6 months'
    ) as pa_sistolica_promedio_6_meses,
    
    -- Número de mediciones en los últimos 6 meses
    (
        SELECT COUNT(*)
        FROM Documento_Subido ds
        WHERE ds.id_usuario = u.id_usuario
        AND ds.fecha_subido >= CURRENT_DATE - INTERVAL '6 months'
    ) as mediciones_6_meses,
    
    -- Frecuencia de mediciones (días entre mediciones)
    (
        SELECT AVG(EXTRACT(DAYS FROM (ds2.fecha_subido - ds1.fecha_subido)))
        FROM Documento_Subido ds1
        JOIN Documento_Subido ds2 ON ds1.id_usuario = ds2.id_usuario
        WHERE ds1.id_usuario = u.id_usuario
        AND ds2.fecha_subido > ds1.fecha_subido
        AND ds1.fecha_subido >= CURRENT_DATE - INTERVAL '6 months'
    ) as dias_promedio_entre_mediciones

FROM Usuario u
WHERE u.id_usuario = p_usuario_id
AND EXISTS (
    SELECT 1 FROM Documento_Subido ds_check 
    WHERE ds_check.id_usuario = u.id_usuario
);
END;
$$;

-- =====================================================
-- 4. STORED PROCEDURE: ANÁLISIS DE RIESGO BASADO EN DATOS REALES
-- =====================================================
-- TÍTULO: Stored Procedure para Análisis de Riesgo Basado en Datos Médicos Reales
-- DESCRIPCIÓN: Analiza el riesgo basado en valores reales de laboratorios

CREATE OR REPLACE PROCEDURE analizar_riesgo_datos_reales(
    p_usuario_id UUID,
    INOUT resultado REFCURSOR
)
LANGUAGE plpgsql AS $$
BEGIN
    OPEN resultado FOR
    SELECT 
    u.id_usuario,
    u.email,
    
    -- Análisis de riesgo cardiovascular basado en HbA1c
    CASE 
        WHEN (
            SELECT hg.hba1c 
            FROM Hemoglobina_Glicada hg
            JOIN Documento_Subido ds ON hg.id_subido = ds.id_subido
            WHERE ds.id_usuario = u.id_usuario
            ORDER BY ds.fecha_subido DESC
            LIMIT 1
        ) >= 6.5 THEN 'Alto riesgo cardiovascular (HbA1c ≥ 6.5%)'
        WHEN (
            SELECT hg.hba1c 
            FROM Hemoglobina_Glicada hg
            JOIN Documento_Subido ds ON hg.id_subido = ds.id_subido
            WHERE ds.id_usuario = u.id_usuario
            ORDER BY ds.fecha_subido DESC
            LIMIT 1
        ) BETWEEN 5.7 AND 6.4 THEN 'Riesgo cardiovascular moderado (HbA1c 5.7-6.4%)'
        WHEN (
            SELECT hg.hba1c 
            FROM Hemoglobina_Glicada hg
            JOIN Documento_Subido ds ON hg.id_subido = ds.id_subido
            WHERE ds.id_usuario = u.id_usuario
            ORDER BY ds.fecha_subido DESC
            LIMIT 1
        ) < 5.7 THEN 'Bajo riesgo cardiovascular (HbA1c < 5.7%)'
        ELSE 'Sin datos de HbA1c'
    END as estado_riesgo_cardiovascular_hba1c,
    
    -- Análisis de riesgo cardiovascular basado en colesterol
    CASE 
        WHEN (
            SELECT pl.colesterol_LDL 
            FROM Perfil_Lipidico pl
            JOIN Documento_Subido ds ON pl.id_subido = ds.id_subido
            WHERE ds.id_usuario = u.id_usuario
            ORDER BY ds.fecha_subido DESC
            LIMIT 1
        ) >= 190 THEN 'Alto riesgo cardiovascular (LDL ≥ 190)'
        WHEN (
            SELECT pl.colesterol_LDL 
            FROM Perfil_Lipidico pl
            JOIN Documento_Subido ds ON pl.id_subido = ds.id_subido
            WHERE ds.id_usuario = u.id_usuario
            ORDER BY ds.fecha_subido DESC
            LIMIT 1
        ) BETWEEN 160 AND 189 THEN 'Riesgo moderado (LDL 160-189)'
        WHEN (
            SELECT pl.colesterol_LDL 
            FROM Perfil_Lipidico pl
            JOIN Documento_Subido ds ON pl.id_subido = ds.id_subido
            WHERE ds.id_usuario = u.id_usuario
            ORDER BY ds.fecha_subido DESC
            LIMIT 1
        ) < 160 THEN 'Riesgo bajo (LDL < 160)'
        ELSE 'Sin datos de colesterol'
    END as estado_cardiovascular_ldl,
    
    -- Análisis de función renal
    CASE 
        WHEN (
            SELECT pm.filtrado_glomerular 
            FROM Panel_Metabolico pm
            JOIN Documento_Subido ds ON pm.id_subido = ds.id_subido
            WHERE ds.id_usuario = u.id_usuario
            ORDER BY ds.fecha_subido DESC
            LIMIT 1
        ) < 60 THEN 'Deterioro de función renal (FG < 60)'
        WHEN (
            SELECT pm.filtrado_glomerular 
            FROM Panel_Metabolico pm
            JOIN Documento_Subido ds ON pm.id_subido = ds.id_subido
            WHERE ds.id_usuario = u.id_usuario
            ORDER BY ds.fecha_subido DESC
            LIMIT 1
        ) BETWEEN 60 AND 90 THEN 'Función renal disminuida (FG 60-90)'
        WHEN (
            SELECT pm.filtrado_glomerular 
            FROM Panel_Metabolico pm
            JOIN Documento_Subido ds ON pm.id_subido = ds.id_subido
            WHERE ds.id_usuario = u.id_usuario
            ORDER BY ds.fecha_subido DESC
            LIMIT 1
        ) >= 90 THEN 'Función renal normal (FG ≥ 90)'
        ELSE 'Sin datos de función renal'
    END as estado_funcion_renal,
    
    -- Análisis de presión arterial
    CASE 
        WHEN (
            SELECT mpa.PA_sistólica_promedio 
            FROM Monitoreo_PA mpa
            JOIN Documento_Subido ds ON mpa.id_subido = ds.id_subido
            WHERE ds.id_usuario = u.id_usuario
            ORDER BY ds.fecha_subido DESC
            LIMIT 1
        ) >= 140 THEN 'Hipertensión (PA ≥ 140)'
        WHEN (
            SELECT mpa.PA_sistólica_promedio 
            FROM Monitoreo_PA mpa
            JOIN Documento_Subido ds ON mpa.id_subido = ds.id_subido
            WHERE ds.id_usuario = u.id_usuario
            ORDER BY ds.fecha_subido DESC
            LIMIT 1
        ) BETWEEN 120 AND 139 THEN 'Prehipertensión (PA 120-139)'
        WHEN (
            SELECT mpa.PA_sistólica_promedio 
            FROM Monitoreo_PA mpa
            JOIN Documento_Subido ds ON mpa.id_subido = ds.id_subido
            WHERE ds.id_usuario = u.id_usuario
            ORDER BY ds.fecha_subido DESC
            LIMIT 1
        ) < 120 THEN 'Presión arterial normal (PA < 120)'
        ELSE 'Sin datos de presión arterial'
    END as estado_presion_arterial

FROM Usuario u
WHERE u.id_usuario = p_usuario_id
AND EXISTS (
    SELECT 1 FROM Documento_Subido ds_check 
    WHERE ds_check.id_usuario = u.id_usuario
);
END;
$$;

-- =====================================================
-- 5. STORED PROCEDURE: RESUMEN INTEGRAL DE SALUD CON DATOS REALES
-- =====================================================
-- TÍTULO: Stored Procedure para Resumen Integral de Salud con Datos Médicos Reales
-- DESCRIPCIÓN: Resumen completo que combina todos los datos médicos reales

CREATE OR REPLACE PROCEDURE resumen_integral_salud(
    p_usuario_id UUID,
    INOUT resultado REFCURSOR
)
LANGUAGE plpgsql AS $$
BEGIN
    OPEN resultado FOR
    SELECT 
    u.id_usuario,
    u.email,
    u.creado_en as fecha_registro,
    
    -- Información personal
    dp.fecha_nacimiento,
    EXTRACT(YEAR FROM AGE(CURRENT_DATE, dp.fecha_nacimiento)) as edad,
    dp.sexo,
    
    -- Datos de historial médico
    hm.colesterol as colesterol_historial,
    hm.colesterol_alto as colesterol_alto_historial,
    hm.bmi,
    hm.salud_general,
    hm.fecha as fecha_historial,
    
    -- Datos de estilo de vida
    ev.nivel_actividad_fisica,
    ev.consumo_sal,
    ev.tabaco,
    ev.horas_dormir,
    ev.nivel_estres,
    ev.fecha as fecha_estilo,
    
    -- Datos médicos más recientes
    (
        SELECT hg.hba1c 
        FROM Hemoglobina_Glicada hg
        JOIN Documento_Subido ds ON hg.id_subido = ds.id_subido
        WHERE ds.id_usuario = u.id_usuario
        ORDER BY ds.fecha_subido DESC
        LIMIT 1
    ) as ultima_hba1c,
    
    (
        SELECT mpa.PA_sistólica_promedio 
        FROM Monitoreo_PA mpa
        JOIN Documento_Subido ds ON mpa.id_subido = ds.id_subido
        WHERE ds.id_usuario = u.id_usuario
        ORDER BY ds.fecha_subido DESC
        LIMIT 1
    ) as ultima_pa_sistolica,
    
    (
        SELECT pl.colesterol_LDL 
        FROM Perfil_Lipidico pl
        JOIN Documento_Subido ds ON pl.id_subido = ds.id_subido
        WHERE ds.id_usuario = u.id_usuario
        ORDER BY ds.fecha_subido DESC
        LIMIT 1
    ) as ultimo_ldl,
    
    -- Diagnósticos confirmados
    (
        SELECT c.diabtes_confirmada 
        FROM Consulta c
        JOIN Documento_Subido ds ON c.id_subido = ds.id_subido
        WHERE ds.id_usuario = u.id_usuario
        ORDER BY ds.fecha_subido DESC
        LIMIT 1
    ) as diabetes_confirmada,
    
    (
        SELECT c.hipertencion_confirmada 
        FROM Consulta c
        JOIN Documento_Subido ds ON c.id_subido = ds.id_subido
        WHERE ds.id_usuario = u.id_usuario
        ORDER BY ds.fecha_subido DESC
        LIMIT 1
    ) as hipertension_confirmada,
    
    -- Tratamientos activos
    (
        SELECT c.tratamiento_metformina 
        FROM Consulta c
        JOIN Documento_Subido ds ON c.id_subido = ds.id_subido
        WHERE ds.id_usuario = u.id_usuario
        ORDER BY ds.fecha_subido DESC
        LIMIT 1
    ) as tratamiento_metformina,
    
    -- Complicaciones
    (
        SELECT c.complicacion_retinopatia 
        FROM Consulta c
        JOIN Documento_Subido ds ON c.id_subido = ds.id_subido
        WHERE ds.id_usuario = u.id_usuario
        ORDER BY ds.fecha_subido DESC
        LIMIT 1
    ) as complicacion_retinopatia,
    
    -- Conteo de documentos
    (
        SELECT COUNT(*)
        FROM Documento_Subido ds
        WHERE ds.id_usuario = u.id_usuario
    ) as total_documentos,
    
    -- Fecha del documento más reciente
    (
        SELECT MAX(fecha_subido)
        FROM Documento_Subido ds
        WHERE ds.id_usuario = u.id_usuario
    ) as fecha_ultimo_documento

FROM Usuario u
LEFT JOIN Datos_Personales dp ON u.id_usuario = dp.id_usuario
LEFT JOIN Historial_Medico hm ON u.id_usuario = hm.id_usuario
LEFT JOIN Estilo_Vida ev ON u.id_usuario = ev.id_usuario
WHERE u.id_usuario = p_usuario_id;
END;
$$;

-- =====================================================
-- 6. STORED PROCEDURE: ANÁLISIS DE COMPLIANCE Y ADHERENCIA AL TRATAMIENTO
-- =====================================================
-- TÍTULO: Stored Procedure para Análisis de Compliance y Adherencia al Tratamiento
-- DESCRIPCIÓN: Analiza la adherencia del usuario al tratamiento basado en datos reales

CREATE OR REPLACE PROCEDURE analizar_compliance_adherencia(
    p_usuario_id UUID,
    INOUT resultado REFCURSOR
)
LANGUAGE plpgsql AS $$
BEGIN
    OPEN resultado FOR
    SELECT 
    u.id_usuario,
    u.email,
    
    -- Frecuencia de subida de documentos (adherencia al monitoreo)
    (
        SELECT COUNT(*)
        FROM Documento_Subido ds
        WHERE ds.id_usuario = u.id_usuario
        AND ds.fecha_subido >= CURRENT_DATE - INTERVAL '3 months'
    ) as documentos_ultimos_3_meses,
    
    (
        SELECT COUNT(*)
        FROM Documento_Subido ds
        WHERE ds.id_usuario = u.id_usuario
        AND ds.fecha_subido >= CURRENT_DATE - INTERVAL '6 months'
    ) as documentos_ultimos_6_meses,
    
    -- Días desde el último documento
    (
        SELECT EXTRACT(DAYS FROM (CURRENT_DATE - ds.fecha_subido))
        FROM Documento_Subido ds
        WHERE ds.id_usuario = u.id_usuario
        ORDER BY ds.fecha_subido DESC
        LIMIT 1
    ) as dias_desde_ultimo_documento,
    
    -- Análisis de mejora en parámetros (efectividad del tratamiento)
    CASE 
        WHEN (
            SELECT hg.hba1c 
            FROM Hemoglobina_Glicada hg
            JOIN Documento_Subido ds ON hg.id_subido = ds.id_subido
            WHERE ds.id_usuario = u.id_usuario
            ORDER BY ds.fecha_subido DESC
            LIMIT 1
        ) < (
            SELECT hg2.hba1c 
            FROM Hemoglobina_Glicada hg2
            JOIN Documento_Subido ds2 ON hg2.id_subido = ds2.id_subido
            WHERE ds2.id_usuario = u.id_usuario
            ORDER BY ds2.fecha_subido DESC
            OFFSET 1
            LIMIT 1
        ) THEN 'Mejora en HbA1c'
        WHEN (
            SELECT hg.hba1c 
            FROM Hemoglobina_Glicada hg
            JOIN Documento_Subido ds ON hg.id_subido = ds.id_subido
            WHERE ds.id_usuario = u.id_usuario
            ORDER BY ds.fecha_subido DESC
            LIMIT 1
        ) > (
            SELECT hg2.hba1c 
            FROM Hemoglobina_Glicada hg2
            JOIN Documento_Subido ds2 ON hg2.id_subido = ds2.id_subido
            WHERE ds2.id_usuario = u.id_usuario
            ORDER BY ds2.fecha_subido DESC
            OFFSET 1
            LIMIT 1
        ) THEN 'Empeoramiento en HbA1c'
        ELSE 'Sin cambios significativos en HbA1c'
    END as tendencia_hba1c,
    
    -- Análisis de mejora en presión arterial
    CASE 
        WHEN (
            SELECT mpa.PA_sistólica_promedio 
            FROM Monitoreo_PA mpa
            JOIN Documento_Subido ds ON mpa.id_subido = ds.id_subido
            WHERE ds.id_usuario = u.id_usuario
            ORDER BY ds.fecha_subido DESC
            LIMIT 1
        ) < (
            SELECT mpa2.PA_sistólica_promedio 
            FROM Monitoreo_PA mpa2
            JOIN Documento_Subido ds2 ON mpa2.id_subido = ds2.id_subido
            WHERE ds2.id_usuario = u.id_usuario
            ORDER BY ds2.fecha_subido DESC
            OFFSET 1
            LIMIT 1
        ) THEN 'Mejora en presión arterial'
        WHEN (
            SELECT mpa.PA_sistólica_promedio 
            FROM Monitoreo_PA mpa
            JOIN Documento_Subido ds ON mpa.id_subido = ds.id_subido
            WHERE ds.id_usuario = u.id_usuario
            ORDER BY ds.fecha_subido DESC
            LIMIT 1
        ) > (
            SELECT mpa2.PA_sistólica_promedio 
            FROM Monitoreo_PA mpa2
            JOIN Documento_Subido ds2 ON mpa2.id_subido = ds2.id_subido
            WHERE ds2.id_usuario = u.id_usuario
            ORDER BY ds2.fecha_subido DESC
            OFFSET 1
            LIMIT 1
        ) THEN 'Empeoramiento en presión arterial'
        ELSE 'Sin cambios significativos en presión arterial'
    END as tendencia_presion_arterial,
    
    -- Nivel de adherencia basado en frecuencia de documentos
    CASE 
        WHEN (
            SELECT COUNT(*)
            FROM Documento_Subido ds
            WHERE ds.id_usuario = u.id_usuario
            AND ds.fecha_subido >= CURRENT_DATE - INTERVAL '3 months'
        ) >= 4 THEN 'Alta adherencia (≥4 documentos en 3 meses)'
        WHEN (
            SELECT COUNT(*)
            FROM Documento_Subido ds
            WHERE ds.id_usuario = u.id_usuario
            AND ds.fecha_subido >= CURRENT_DATE - INTERVAL '3 months'
        ) BETWEEN 2 AND 3 THEN 'Adherencia moderada (2-3 documentos en 3 meses)'
        WHEN (
            SELECT COUNT(*)
            FROM Documento_Subido ds
            WHERE ds.id_usuario = u.id_usuario
            AND ds.fecha_subido >= CURRENT_DATE - INTERVAL '3 months'
        ) = 1 THEN 'Baja adherencia (1 documento en 3 meses)'
        ELSE 'Sin adherencia (0 documentos en 3 meses)'
    END as nivel_adherencia,
    
    -- Recomendación basada en adherencia
    CASE 
        WHEN (
            SELECT EXTRACT(DAYS FROM (CURRENT_DATE - ds.fecha_subido))
            FROM Documento_Subido ds
            WHERE ds.id_usuario = u.id_usuario
            ORDER BY ds.fecha_subido DESC
            LIMIT 1
        ) > 90 THEN 'Urgente: Subir nuevos documentos médicos'
        WHEN (
            SELECT EXTRACT(DAYS FROM (CURRENT_DATE - ds.fecha_subido))
            FROM Documento_Subido ds
            WHERE ds.id_usuario = u.id_usuario
            ORDER BY ds.fecha_subido DESC
            LIMIT 1
        ) BETWEEN 60 AND 90 THEN 'Recomendado: Subir documentos médicos pronto'
        WHEN (
            SELECT COUNT(*)
            FROM Documento_Subido ds
            WHERE ds.id_usuario = u.id_usuario
            AND ds.fecha_subido >= CURRENT_DATE - INTERVAL '3 months'
        ) < 2 THEN 'Mejorar adherencia: Subir más documentos regularmente'
        ELSE 'Mantener adherencia actual'
    END as recomendacion_adherencia

FROM Usuario u
WHERE u.id_usuario = p_usuario_id
AND EXISTS (
    SELECT 1 FROM Documento_Subido ds_check 
    WHERE ds_check.id_usuario = u.id_usuario
);
END;
$$;

